import { isDeprecated } from '../filters/index.js';
export const deprecatedJson = (outputFile, prefix, buildPath) => ({
    prefix,
    buildPath,
    transforms: ['name/pathToDotNotation', 'json/deprecated'],
    files: [
        {
            destination: outputFile,
            format: `json/flat`,
            filter: isDeprecated,
        },
    ],
});
